config.script:

        return function(viewer, element, parentModule){

        var me=this;
        var div = new Element('div',{"class":"polygon-overlay-menu"});


            <?php 
                GetPlugin('Maps');
                $layers=array_values(array_filter(MapController::GetAllLayers(), function($l){return $l->getId()>=25&&$l->getId()<36;}));
                $polyconfig=GetWidget('polygon-config');
            ?>

            var layersMeta=<?php echo json_encode(array_map(function($l){ return $l->getMetadata(); }, $layers)); ?>;
            var names=<?php echo json_encode(array_map(function($l){ return $l->getId().' - '.$l->getName(); }, $layers)); ?>;
            var icons=<?php echo json_encode(array_map(function($l) use($polyconfig){ 

                $icon= $l->getIcon(); 
                $icon='components/com_geolive/users_files/user_files_680/Uploads/[G]_jq3_[ImAgE]_1VS_oJg.png';

                $configicon=$polyconfig->getParameter('layer-'.$l->getId());
                if(!empty($configicon)){
                    $icon=$configicon[0];
                }
                return $icon;

            }, $layers)); ?>;

            var iconSelection = new UIIconizedSelectionControl(div, {
              allowMultipleSelection: true,
              allowEmptySelection: true,
              icons: icons

            });

            iconSelection.addEvent('loadIcon', function(icon, i, asset) {
              asset.parentNode.setAttribute('data-label',Localize(names[i].capitalize(),'layer_'+layersMeta[i].id+'_title'));
              asset.parentNode.addClass('layer'+layersMeta[i].id);
            }); 


            iconSelection.addEvent('selectionChanged', function(selection) {

                //hide show layers
                window.overlayLayers.forEach(function(l,i){
                    l.hide();
                });
                selection.forEach(function(s,i){
                    window.overlayLayers[s[1]].show();
                });


                viewer.getUIView().getDropDownMenu().hide();
                if(!selection.length){
                     viewer.getUIView().getTab().setAttribute('data-count-enabled-layers',null);
                }else{
                     viewer.getUIView().getTab().setAttribute('data-count-enabled-layers', selection.length);
                }

                //TODO: close panel.
                //Display indicator

            });




var OverlayLayer = (function() {

	if(!window.GeoliveLayer){
		if(window.console&&console.warn){
			console.warn('GeoliveLayer is not defined');
		}
		return null;
	}

	return new Class({
		Extends: GeoliveLayer
	});



})()


if(!window.overlayLayers){
    window.overlayLayers=layersMeta.map(function(meta){
        return new OverlayLayer(application, Object.append(meta,{initialVisibility:"later"}));
    });
}



            element.appendChild(div);



}
