config.script:
var legend = new Element('div', {
    "class":"legend"
});

var toggle=legend.appendChild(new Element('div',{"class":"toggle", events:{click:function(){

    if(legend.hasClass('minimized')){
        legend.removeClass('minimized');
        return;
    }
    legend.addClass('minimized');

}}}));


legend.appendChild(new Element('h1',{"html":"Legend"}));


var periods={
             "habsburg-empire-until-1914":"#ff8b48",
             "world-war-ii-19411945":"#b83737",
             "divided-city-italyyugoslavia-19241941":"#a3b851",
             "republic-of-croatia-1991-today":"#257339",
             "world-war-i-19141918":"#1d0b73",
             "socialist-yugoslavia-19451991":"#625028"
        };

var categories={
             "culture--education":"",
             "industry":"",
             "infrastructure--militarygovernment":"",
             "leisure-time-spaces":"",
             "public-spaces--monuments":"",
             "residential":""

};

var periodsEl=legend.appendChild(new Element('div',{"class":"periods"}));
periodsEl.appendChild(new Element('h2',{"html":"Periods"}));

Object.keys(periods).forEach(function(p){
    var pEl=periodsEl.appendChild(new Element('div',{"class":"period "+p}));
    pEl.setStyle('background-color', periods[p]);
    new UIPopover(pEl, {
        content:p
    });
});


var categoriesEl=legend.appendChild(new Element('div',{"class":"categories"}));
categoriesEl.appendChild(new Element('h2',{"html":"Categories"}));

Object.keys(categories).forEach(function(c){
    var cEl = categoriesEl.appendChild(new Element('div',{"class":"category "+c}));
    cEl.setStyle("background-image","https://rijekafiume.geolive.ca/images/slideshow/markers/"+c+".png");
    new UIPopover(cEl, {
        content:c
    });
});


return legend;

